            },
            blur:function(){
                var sel = this.selection.getNative();
                if(sel.empty && browser.ie){
                    var nativeRng = document.body.createTextRange();
                    nativeRng.moveToElementText(document.body);
                    nativeRng.collapse(true);
                    nativeRng.select();
                    sel.empty()
                }else{
                    sel.removeAllRanges()
                }

