            utils.each(domUtils.getElementsByTagName(common, 'span'), function (span) {
                if (!span.parentNode || domUtils.isBookmarkNode(span))return;
                if (/\s*border\s*:\s*none;?\s*/i.test(span.style.cssText)) {
