    UE.plugin.register('wordimage',function(){
        var me = this,
            images = [];
        return {
            commands : {
                'wordimage':{
                    execCommand:function () {
                        var images = domUtils.getElementsByTagName(me.body, "img");
                        var urlList = [];
                        for (var i = 0, ci; ci = images[i++];) {
                            var url = ci.getAttribute("word_img");
                            url && urlList.push(url);
                        }
                        return urlList;
                    },
                    queryCommandState:function () {
                        images = domUtils.getElementsByTagName(me.body, "img");
                        for (var i = 0, ci; ci = images[i++];) {
                            if (ci.getAttribute("word_img")) {
                                return 1;
                            }
                        }
                        return -1;
                    },
                    notNeedUndo:true
                }
            },
            inputRule : function (root) {
                utils.each(root.getNodesByTagName('img'), function (img) {
                    var attrs = img.attrs,
                        flag = parseInt(attrs.width) < 128 || parseInt(attrs.height) < 43,
                        opt = me.options,
                        src = opt.UEDITOR_HOME_URL + 'themes/default/images/spacer.gif';
                    if (attrs['src'] && /^(?:(file:\/+))/.test(attrs['src'])) {
                        img.setAttr({
                            width:attrs.width,
                            height:attrs.height,
                            alt:attrs.alt,
                            word_img: attrs.src,
                            src:src,
                            'style':'background:url(' + ( flag ? opt.themePath + opt.theme + '/images/word.gif' : opt.langPath + opt.lang + '/images/localimage.png') + ') no-repeat center center;border:1px solid #ddd'
                        })
                    }
                })
            }
        }
    });

// plugins/dragdrop.js
    UE.plugins['dragdrop'] = function (){

        var me = this;
        me.ready(function(){
            domUtils.on(this.body,'dragend',function(){
                var rng = me.selection.getRange();
                var node = rng.getClosedNode()||me.selection.getStart();

                if(node && node.tagName == 'IMG'){

                    var pre = node.previousSibling,next;
                    while(next = node.nextSibling){
                        if(next.nodeType == 1 && next.tagName == 'SPAN' && !next.firstChild){
                            domUtils.remove(next)
                        }else{
                            break;
                        }
                    }


                    if((pre && pre.nodeType == 1 && !domUtils.isEmptyBlock(pre) || !pre) && (!next || next && !domUtils.isEmptyBlock(next))){
                        if(pre && pre.tagName == 'P' && !domUtils.isEmptyBlock(pre)){
                            pre.appendChild(node);
                            domUtils.moveChild(next,pre);
                            domUtils.remove(next);
                        }else  if(next && next.tagName == 'P' && !domUtils.isEmptyBlock(next)){
                            next.insertBefore(node,next.firstChild);
                        }

                        if(pre && pre.tagName == 'P' && domUtils.isEmptyBlock(pre)){
                            domUtils.remove(pre)
                        }
                        if(next && next.tagName == 'P' && domUtils.isEmptyBlock(next)){
                            domUtils.remove(next)
                        }
                        rng.selectNode(node).select();
                        me.fireEvent('saveScene');

                    }

                }

            })
        });
        me.addListener('keyup', function(type, evt) {
            var keyCode = evt.keyCode || evt.which;
            if (keyCode == 13) {
                var rng = me.selection.getRange(),node;
                if(node = domUtils.findParentByTagName(rng.startContainer,'p',true)){
                    if(domUtils.getComputedStyle(node,'text-align') == 'center'){
                        domUtils.removeStyle(node,'text-align')
                    }
                }
            }
        })
    };


// plugins/undo.js
    /**
     * undo redo
     * @file
     * @since 1.2.6.1
     */

    /**
     * 撤销上一次执行的命令
     * @command undo
     * @method execCommand
     * @param { String } cmd 命令字符串
     * @example
     * ```javascript
     * editor.execCommand( 'undo' );
     * ```
     */

    /**
     * 重做上一次执行的命令
     * @command redo
     * @method execCommand
     * @param { String } cmd 命令字符串
     * @example
     * ```javascript
     * editor.execCommand( 'redo' );
     * ```
     */

    UE.plugins['undo'] = function () {
        var saveSceneTimer;
        var me = this,
            maxUndoCount = me.options.maxUndoCount || 20,
            maxInputCount = me.options.maxInputCount || 20,
            fillchar = new RegExp(domUtils.fillChar + '|<\/hr>', 'gi');// ie会产生多余的</hr>
        var noNeedFillCharTags = {
            ol:1,ul:1,table:1,tbody:1,tr:1,body:1
        };
        var orgState = me.options.autoClearEmptyNode;
        function compareAddr(indexA, indexB) {
            if (indexA.length != indexB.length)
                return 0;
            for (var i = 0, l = indexA.length; i < l; i++) {
                if (indexA[i] != indexB[i])
                    return 0
            }
            return 1;
        }

        function compareRangeAddress(rngAddrA, rngAddrB) {
            if (rngAddrA.collapsed != rngAddrB.collapsed) {
                return 0;
            }
            if (!compareAddr(rngAddrA.startAddress, rngAddrB.startAddress) || !compareAddr(rngAddrA.endAddress, rngAddrB.endAddress)) {
                return 0;
            }
            return 1;
        }

        function UndoManager() {
            this.list = [];
            this.index = 0;
            this.hasUndo = false;
            this.hasRedo = false;
            this.undo = function () {
                if (this.hasUndo) {
                    if (!this.list[this.index - 1] && this.list.length == 1) {
                        this.reset();
                        return;
                    }
                    while (this.list[this.index].content == this.list[this.index - 1].content) {
                        this.index--;
                        if (this.index == 0) {
                            return this.restore(0);
                        }
                    }
                    this.restore(--this.index);
                }
            };
            this.redo = function () {
                if (this.hasRedo) {
                    while (this.list[this.index].content == this.list[this.index + 1].content) {
                        this.index++;
                        if (this.index == this.list.length - 1) {
                            return this.restore(this.index);
                        }
                    }
                    this.restore(++this.index);
                }
            };

            this.restore = function () {
                var me = this.editor;
                var scene = this.list[this.index];
                var root = UE.htmlparser(scene.content.replace(fillchar, ''));
                me.options.autoClearEmptyNode = false;
                me.filterInputRule(root);
                me.options.autoClearEmptyNode = orgState;
                //trace:873
                //去掉展位符
                me.document.body.innerHTML = root.toHtml();
                me.fireEvent('afterscencerestore');
                //处理undo后空格不展位的问题
                if (browser.ie) {
                    utils.each(domUtils.getElementsByTagName(me.document,'td th caption p'),function(node){
                        if(domUtils.isEmptyNode(node)){
                            domUtils.fillNode(me.document, node);
                        }
                    })
                }

                try{
                    var rng = new dom.Range(me.document).moveToAddress(scene.address);
                    rng.select(noNeedFillCharTags[rng.startContainer.nodeName.toLowerCase()]);
                }catch(e){}

                this.update();
                this.clearKey();
                //不能把自己reset了
                me.fireEvent('reset', true);
            };

            this.getScene = function () {
                var me = this.editor;
                var rng = me.selection.getRange(),
                    rngAddress = rng.createAddress(false,true);
                me.fireEvent('beforegetscene');
                var root = UE.htmlparser(me.body.innerHTML);
                me.options.autoClearEmptyNode = false;
                me.filterOutputRule(root);
                me.options.autoClearEmptyNode = orgState;
                var cont = root.toHtml();
                //trace:3461
                //这个会引起回退时导致空格丢失的情况
//            browser.ie && (cont = cont.replace(/>&nbsp;</g, '><').replace(/\s*</g, '<').replace(/>\s*/g, '>'));
                me.fireEvent('aftergetscene');

                return {
                    address:rngAddress,
                    content:cont
                }
            };
            this.save = function (notCompareRange,notSetCursor) {
                clearTimeout(saveSceneTimer);
                var currentScene = this.getScene(notSetCursor),
                    lastScene = this.list[this.index];

                if(lastScene && lastScene.content != currentScene.content){
                    me.trigger('contentchange')
                }
                //内容相同位置相同不存
                if (lastScene && lastScene.content == currentScene.content &&
                    ( notCompareRange ? 1 : compareRangeAddress(lastScene.address, currentScene.address) )
                ) {
                    return;
                }
                this.list = this.list.slice(0, this.index + 1);
                this.list.push(currentScene);
                //如果大于最大数量了，就把最前的剔除
                if (this.list.length > maxUndoCount) {
                    this.list.shift();
                }
                this.index = this.list.length - 1;
                this.clearKey();
                //跟新undo/redo状态
                this.update();

            };
            this.update = function () {
                this.hasRedo = !!this.list[this.index + 1];
                this.hasUndo = !!this.list[this.index - 1];
            };
            this.reset = function () {
                this.list = [];
                this.index = 0;
                this.hasUndo = false;
                this.hasRedo = false;
                this.clearKey();
            };
            this.clearKey = function () {
                keycont = 0;
                lastKeyCode = null;
            };
        }

        me.undoManger = new UndoManager();
        me.undoManger.editor = me;
        function saveScene() {
            this.undoManger.save();
        }

        me.addListener('saveScene', function () {
            var args = Array.prototype.splice.call(arguments,1);
            this.undoManger.save.apply(this.undoManger,args);
        });

//    me.addListener('beforeexeccommand', saveScene);
//    me.addListener('afterexeccommand', saveScene);

        me.addListener('reset', function (type, exclude) {
            if (!exclude) {
                this.undoManger.reset();
            }
        });
        me.commands['redo'] = me.commands['undo'] = {
            execCommand:function (cmdName) {
                this.undoManger[cmdName]();
            },
            queryCommandState:function (cmdName) {
                return this.undoManger['has' + (cmdName.toLowerCase() == 'undo' ? 'Undo' : 'Redo')] ? 0 : -1;
            },
            notNeedUndo:1
        };

        var keys = {
                //  /*Backspace*/ 8:1, /*Delete*/ 46:1,
                /*Shift*/ 16:1, /*Ctrl*/ 17:1, /*Alt*/ 18:1,
                37:1, 38:1, 39:1, 40:1

            },
            keycont = 0,
            lastKeyCode;
        //输入法状态下不计算字符数
        var inputType = false;
        me.addListener('ready', function () {
            domUtils.on(this.body, 'compositionstart', function () {
                inputType = true;
            });
            domUtils.on(this.body, 'compositionend', function () {
                inputType = false;
            })
        });
        //快捷键
        me.addshortcutkey({
            "Undo":"ctrl+90", //undo
            "Redo":"ctrl+89" //redo

        });
        var isCollapsed = true;
        me.addListener('keydown', function (type, evt) {

            var me = this;
            var keyCode = evt.keyCode || evt.which;
            if (!keys[keyCode] && !evt.ctrlKey && !evt.metaKey && !evt.shiftKey && !evt.altKey) {
                if (inputType)
                    return;

                if(!me.selection.getRange().collapsed){
                    me.undoManger.save(false,true);
                    isCollapsed = false;
                    return;
                }
                if (me.undoManger.list.length == 0) {
                    me.undoManger.save(true);
                }
                clearTimeout(saveSceneTimer);
                function save(cont){
                    cont.undoManger.save(false,true);
                    cont.fireEvent('selectionchange');
                }
                saveSceneTimer = setTimeout(function(){
                    if(inputType){
                        var interalTimer = setInterval(function(){
                            if(!inputType){
                                save(me);
                                clearInterval(interalTimer)
                            }
                        },300)
                        return;
                    }
                    save(me);
                },200);

                lastKeyCode = keyCode;
                keycont++;
                if (keycont >= maxInputCount ) {
                    save(me)
                }
            }
        });
        me.addListener('keyup', function (type, evt) {
            var keyCode = evt.keyCode || evt.which;
            if (!keys[keyCode] && !evt.ctrlKey && !evt.metaKey && !evt.shiftKey && !evt.altKey) {
                if (inputType)
                    return;
                if(!isCollapsed){
                    this.undoManger.save(false,true);
                    isCollapsed = true;
                }
            }
        });
        //扩展实例，添加关闭和开启命令undo
        me.stopCmdUndo = function(){
            me.__hasEnterExecCommand = true;
        };
        me.startCmdUndo = function(){
            me.__hasEnterExecCommand = false;
        }
    };


// plugins/copy.js
    UE.plugin.register('copy', function () {

        var me = this;

        function initZeroClipboard() {

            ZeroClipboard.config({
                debug: false,
                swfPath: me.options.UEDITOR_HOME_URL + 'third-party/zeroclipboard/ZeroClipboard.swf'
            });

            var client = me.zeroclipboard = new ZeroClipboard();

            // 复制内容
            client.on('copy', function (e) {
                var client = e.client,
                    rng = me.selection.getRange(),
                    div = document.createElement('div');

                div.appendChild(rng.cloneContents());
                client.setText(div.innerText || div.textContent);
                client.setHtml(div.innerHTML);
                rng.select();
            });
            // hover事件传递到target
            client.on('mouseover mouseout', function (e) {
                var target = e.target;
                if (e.type == 'mouseover') {
                    domUtils.addClass(target, 'edui-state-hover');
                } else if (e.type == 'mouseout') {
                    domUtils.removeClasses(target, 'edui-state-hover');
                }
            });
            // flash加载不成功
            client.on('wrongflash noflash', function () {
                ZeroClipboard.destroy();
            });
        }

        return {
            bindEvents: {
                'ready': function () {
                    if (!browser.ie) {
                        if (window.ZeroClipboard) {
                            initZeroClipboard();
                        } else {
                            utils.loadFile(document, {
                                src: me.options.UEDITOR_HOME_URL + "third-party/zeroclipboard/ZeroClipboard.js",
                                tag: "script",
                                type: "text/javascript",
                                defer: "defer"
                            }, function () {
                                initZeroClipboard();
                            });
                        }
                    }
                }
            },
            commands: {
                'copy': {
                    execCommand: function (cmd) {
                        if (!me.document.execCommand('copy')) {
                            alert(me.getLang('copymsg'));
                        }
                    }
                }
            }
        }
    });


// plugins/paste.js
///import core
///import plugins/inserthtml.js
///import plugins/undo.js
///import plugins/serialize.js
///commands 粘贴
///commandsName  PastePlain
///commandsTitle  纯文本粘贴模式
    /**
     * @description 粘贴
     * @author zhanyi
     */
    UE.plugins['paste'] = function () {
        function getClipboardData(callback) {
            var doc = this.document;
            if (doc.getElementById('baidu_pastebin')) {
                return;
            }
            var range = this.selection.getRange(),
                bk = range.createBookmark(),
                //创建剪贴的容器div
                pastebin = doc.createElement('div');
            pastebin.id = 'baidu_pastebin';
            // Safari 要求div必须有内容，才能粘贴内容进来
            browser.webkit && pastebin.appendChild(doc.createTextNode(domUtils.fillChar + domUtils.fillChar));
            doc.body.appendChild(pastebin);
            //trace:717 隐藏的span不能得到top
            //bk.start.innerHTML = '&nbsp;';
            bk.start.style.display = '';
            pastebin.style.cssText = "position:absolute;width:1px;height:1px;overflow:hidden;left:-1000px;white-space:nowrap;top:" +
                //要在现在光标平行的位置加入，否则会出现跳动的问题
                domUtils.getXY(bk.start).y + 'px';

            range.selectNodeContents(pastebin).select(true);

            setTimeout(function () {
                if (browser.webkit) {
                    for (var i = 0, pastebins = doc.querySelectorAll('#baidu_pastebin'), pi; pi = pastebins[i++];) {
                        if (domUtils.isEmptyNode(pi)) {
                            domUtils.remove(pi);
                        } else {
                            pastebin = pi;
                            break;
                        }
                    }
                }
                try {
                    pastebin.parentNode.removeChild(pastebin);
                } catch (e) {
                }
                range.moveToBookmark(bk).select(true);
                callback(pastebin);
            }, 0);
        }

        var me = this;

        me.setOpt({
            retainOnlyLabelPasted : false
        });

        var txtContent, htmlContent, address;

        function getPureHtml(html){
            return html.replace(/<(\/?)([\w\-]+)([^>]*)>/gi, function (a, b, tagName, attrs) {
                tagName = tagName.toLowerCase();
                if ({img: 1}[tagName]) {
                    return a;
                }
                attrs = attrs.replace(/([\w\-]*?)\s*=\s*(("([^"]*)")|('([^']*)')|([^\s>]+))/gi, function (str, atr, val) {
                    if ({
                            'src': 1,
                            'href': 1,
                            'name': 1
                        }[atr.toLowerCase()]) {
                        return atr + '=' + val + ' '
                    }
                    return ''
                });
                if ({
                        'span': 1,
                        'div': 1
                    }[tagName]) {
                    return ''
                } else {

                    return '<' + b + tagName + ' ' + utils.trim(attrs) + '>'
                }

            });
        }
        function filter(div) {
            var html;
            if (div.firstChild) {
                //去掉cut中添加的边界值
                var nodes = domUtils.getElementsByTagName(div, 'span');
                for (var i = 0, ni; ni = nodes[i++];) {
                    if (ni.id == '_baidu_cut_start' || ni.id == '_baidu_cut_end') {
                        domUtils.remove(ni);
                    }
                }

                if (browser.webkit) {

                    var brs = div.querySelectorAll('div br');
                    for (var i = 0, bi; bi = brs[i++];) {
                        var pN = bi.parentNode;
                        if (pN.tagName == 'DIV' && pN.childNodes.length == 1) {
                            pN.innerHTML = '<p><br/></p>';
                            domUtils.remove(pN);
                        }
                    }
                    var divs = div.querySelectorAll('#baidu_pastebin');
                    for (var i = 0, di; di = divs[i++];) {
                        var tmpP = me.document.createElement('p');
                        di.parentNode.insertBefore(tmpP, di);
                        while (di.firstChild) {
                            tmpP.appendChild(di.firstChild);
                        }
                        domUtils.remove(di);
                    }

                    var metas = div.querySelectorAll('meta');
                    for (var i = 0, ci; ci = metas[i++];) {
                        domUtils.remove(ci);
                    }

                    var brs = div.querySelectorAll('br');
                    for (i = 0; ci = brs[i++];) {
                        if (/^apple-/i.test(ci.className)) {
                            domUtils.remove(ci);
                        }
                    }
                }
                if (browser.gecko) {
                    var dirtyNodes = div.querySelectorAll('[_moz_dirty]');
                    for (i = 0; ci = dirtyNodes[i++];) {
                        ci.removeAttribute('_moz_dirty');
                    }
                }
                if (!browser.ie) {
                    var spans = div.querySelectorAll('span.Apple-style-span');
                    for (var i = 0, ci; ci = spans[i++];) {
                        domUtils.remove(ci, true);
                    }
                }

                //ie下使用innerHTML会产生多余的\r\n字符，也会产生&nbsp;这里过滤掉
                html = div.innerHTML;//.replace(/>(?:(\s|&nbsp;)*?)</g,'><');

                //过滤word粘贴过来的冗余属性
                html = UE.filterWord(html);
                //取消了忽略空白的第二个参数，粘贴过来的有些是有空白的，会被套上相关的标签
                var root = UE.htmlparser(html);
                //如果给了过滤规则就先进行过滤
                if (me.options.filterRules) {
                    UE.filterNode(root, me.options.filterRules);
                }
                //执行默认的处理
                me.filterInputRule(root);
                //针对chrome的处理
                if (browser.webkit) {
                    var br = root.lastChild();
                    if (br && br.type == 'element' && br.tagName == 'br') {
                        root.removeChild(br)
                    }
                    utils.each(me.body.querySelectorAll('div'), function (node) {
                        if (domUtils.isEmptyBlock(node)) {
                            domUtils.remove(node,true)
                        }
                    })
                }
                html = {'html': root.toHtml()};
                me.fireEvent('beforepaste', html, root);
                //抢了默认的粘贴，那后边的内容就不执行了，比如表格粘贴
                if(!html.html){
                    return;
                }
                root = UE.htmlparser(html.html,true);
                //如果开启了纯文本模式
                if (me.queryCommandState('pasteplain') === 1) {
                    me.execCommand('insertHtml', UE.filterNode(root, me.options.filterTxtRules).toHtml(), true);
                } else {
                    //文本模式
                    UE.filterNode(root, me.options.filterTxtRules);
                    txtContent = root.toHtml();
                    //完全模式
                    htmlContent = html.html;

                    address = me.selection.getRange().createAddress(true);
                    me.execCommand('insertHtml', me.getOpt('retainOnlyLabelPasted') === true ?  getPureHtml(htmlContent) : htmlContent, true);
                }
                me.fireEvent("afterpaste", html);
            }
        }

        me.addListener('pasteTransfer', function (cmd, plainType) {

            if (address && txtContent && htmlContent && txtContent != htmlContent) {
                var range = me.selection.getRange();
                range.moveToAddress(address, true);

                if (!range.collapsed) {

                    while (!domUtils.isBody(range.startContainer)
                        ) {
                        var start = range.startContainer;
                        if(start.nodeType == 1){
                            start = start.childNodes[range.startOffset];
                            if(!start){
                                range.setStartBefore(range.startContainer);
                                continue;
                            }
                            var pre = start.previousSibling;

                            if(pre && pre.nodeType == 3 && new RegExp('^[\n\r\t '+domUtils.fillChar+']*$').test(pre.nodeValue)){
                                range.setStartBefore(pre)
                            }
                        }
                        if(range.startOffset == 0){
                            range.setStartBefore(range.startContainer);
                        }else{
                            break;
                        }

                    }
                    while (!domUtils.isBody(range.endContainer)
                        ) {
                        var end = range.endContainer;
                        if(end.nodeType == 1){
                            end = end.childNodes[range.endOffset];
                            if(!end){
                                range.setEndAfter(range.endContainer);
                                continue;
                            }
                            var next = end.nextSibling;
                            if(next && next.nodeType == 3 && new RegExp('^[\n\r\t'+domUtils.fillChar+']*$').test(next.nodeValue)){
                                range.setEndAfter(next)
                            }
                        }
                        if(range.endOffset == range.endContainer[range.endContainer.nodeType == 3 ? 'nodeValue' : 'childNodes'].length){
                            range.setEndAfter(range.endContainer);
                        }else{
                            break;
                        }

                    }

                }

                range.deleteContents();
                range.select(true);
                me.__hasEnterExecCommand = true;
                var html = htmlContent;
                if (plainType === 2 ) {
                    html = getPureHtml(html);
                } else if (plainType) {
                    html = txtContent;
                }
                me.execCommand('inserthtml', html, true);
                me.__hasEnterExecCommand = false;
                var rng = me.selection.getRange();
                while (!domUtils.isBody(rng.startContainer) && !rng.startOffset &&
                    rng.startContainer[rng.startContainer.nodeType == 3 ? 'nodeValue' : 'childNodes'].length
                    ) {
                    rng.setStartBefore(rng.startContainer);
                }
                var tmpAddress = rng.createAddress(true);
                address.endAddress = tmpAddress.startAddress;
            }
        });

        me.addListener('ready', function () {
            domUtils.on(me.body, 'cut', function () {
                var range = me.selection.getRange();
                if (!range.collapsed && me.undoManger) {
                    me.undoManger.save();
                }
            });

            //ie下beforepaste在点击右键时也会触发，所以用监控键盘才处理
            domUtils.on(me.body, browser.ie || browser.opera ? 'keydown' : 'paste', function (e) {
                if ((browser.ie || browser.opera) && ((!e.ctrlKey && !e.metaKey) || e.keyCode != '86')) {
                    return;
                }
                getClipboardData.call(me, function (div) {
                    filter(div);
                });
            });

        });

        me.commands['paste'] = {
            execCommand: function (cmd) {
                if (browser.ie) {
                    getClipboardData.call(me, function (div) {
                        filter(div);
                    });
                    me.document.execCommand('paste');
                } else {
                    alert(me.getLang('pastemsg'));
                }
            }
        }
    };



// plugins/puretxtpaste.js
    /**
     * 纯文本粘贴插件
     * @file
     * @since 1.2.6.1
     */

    UE.plugins['pasteplain'] = function(){
        var me = this;
        me.setOpt({
            'pasteplain':false,
            'filterTxtRules' : function(){
                function transP(node){
                    node.tagName = 'p';
                    node.setStyle();
                }
                function removeNode(node){
                    node.parentNode.removeChild(node,true)
                }
                return {
                    //直接删除及其字节点内容
                    '-' : 'script style object iframe embed input select',
                    'p': {$:{}},
                    'br':{$:{}},
                    div: function (node) {
                        var tmpNode, p = UE.uNode.createElement('p');
                        while (tmpNode = node.firstChild()) {
                            if (tmpNode.type == 'text' || !UE.dom.dtd.$block[tmpNode.tagName]) {
                                p.appendChild(tmpNode);
                            } else {
                                if (p.firstChild()) {
                                    node.parentNode.insertBefore(p, node);
                                    p = UE.uNode.createElement('p');
                                } else {
                                    node.parentNode.insertBefore(tmpNode, node);
                                }
                            }
                        }
                        if (p.firstChild()) {
                            node.parentNode.insertBefore(p, node);
                        }
                        node.parentNode.removeChild(node);
                    },
                    ol: removeNode,
                    ul: removeNode,
                    dl:removeNode,
                    dt:removeNode,
                    dd:removeNode,
                    'li':removeNode,
                    'caption':transP,
                    'th':transP,
                    'tr':transP,
                    'h1':transP,'h2':transP,'h3':transP,'h4':transP,'h5':transP,'h6':transP,
                    'td':function(node){
                        //没有内容的td直接删掉
                        var txt = !!node.innerText();
                        if(txt){
                            node.parentNode.insertAfter(UE.uNode.createText(' &nbsp; &nbsp;'),node);
                        }
                        node.parentNode.removeChild(node,node.innerText())
                    }
                }
            }()
        });
        //暂时这里支持一下老版本的属性
        var pasteplain = me.options.pasteplain;

        /**
         * 启用或取消纯文本粘贴模式
         * @command pasteplain
         * @method execCommand
         * @param { String } cmd 命令字符串
         * @example
         * ```javascript
         * editor.queryCommandState( 'pasteplain' );
         * ```
         */

        /**
         * 查询当前是否处于纯文本粘贴模式
         * @command pasteplain
         * @method queryCommandState
         * @param { String } cmd 命令字符串
         * @return { int } 如果处于纯文本模式，返回1，否则，返回0
         * @example
         * ```javascript
         * editor.queryCommandState( 'pasteplain' );
         * ```
         */
        me.commands['pasteplain'] = {
            queryCommandState: function (){
                return pasteplain ? 1 : 0;
            },
            execCommand: function (){
                pasteplain = !pasteplain|0;
            },
            notNeedUndo : 1
        };
    };

// plugins/list.js
    /**
     * 有序列表,无序列表插件
     * @file
     * @since 1.2.6.1
     */

    UE.plugins['list'] = function () {
        var me = this,
            notExchange = {
                'TD':1,
                'PRE':1,
                'BLOCKQUOTE':1
            };
        var customStyle = {
            'cn' : 'cn-1-',
            'cn1' : 'cn-2-',
            'cn2' : 'cn-3-',
            'num':  'num-1-',
            'num1' : 'num-2-',
            'num2' : 'num-3-',
            'dash'  : 'dash',
            'dot':'dot'
        };

        me.setOpt( {
            'autoTransWordToList':false,
            'insertorderedlist':{
                'num':'',
                'num1':'',
                'num2':'',
                'cn':'',
                'cn1':'',
                'cn2':'',
                'decimal':'',
                'lower-alpha':'',
                'lower-roman':'',
                'upper-alpha':'',
                'upper-roman':''
            },
            'insertunorderedlist':{
                'circle':'',
                'disc':'',
                'square':'',
                'dash' : '',
                'dot':''
            },
            listDefaultPaddingLeft : '30',
            listiconpath : 'http://bs.baidu.com/listicon/',
            maxListLevel : -1,//-1不限制
            disablePInList:false
        } );
        function listToArray(list){
            var arr = [];
            for(var p in list){
                arr.push(p)
            }
            return arr;
        }
        var listStyle = {
            'OL':listToArray(me.options.insertorderedlist),
            'UL':listToArray(me.options.insertunorderedlist)
        };
        var liiconpath = me.options.listiconpath;

        //根据用户配置，调整customStyle
        for(var s in customStyle){
            if(!me.options.insertorderedlist.hasOwnProperty(s) && !me.options.insertunorderedlist.hasOwnProperty(s)){
                delete customStyle[s];
            }
        }

        me.ready(function () {
            var customCss = [];
            for(var p in customStyle){
                if(p == 'dash' || p == 'dot'){
                    customCss.push('li.list-' + customStyle[p] + '{background-image:url(' + liiconpath +customStyle[p]+'.gif)}');
                    customCss.push('ul.custom_'+p+'{list-style:none;}ul.custom_'+p+' li{background-position:0 3px;background-repeat:no-repeat}');
                }else{
                    for(var i= 0;i<99;i++){
                        customCss.push('li.list-' + customStyle[p] + i + '{background-image:url(' + liiconpath + 'list-'+customStyle[p] + i + '.gif)}')
                    }
                    customCss.push('ol.custom_'+p+'{list-style:none;}ol.custom_'+p+' li{background-position:0 3px;background-repeat:no-repeat}');
                }
                switch(p){
                    case 'cn':
                        customCss.push('li.list-'+p+'-paddingleft-1{padding-left:25px}');
                        customCss.push('li.list-'+p+'-paddingleft-2{padding-left:40px}');
                        customCss.push('li.list-'+p+'-paddingleft-3{padding-left:55px}');
                        break;
                    case 'cn1':
                        customCss.push('li.list-'+p+'-paddingleft-1{padding-left:30px}');
                        customCss.push('li.list-'+p+'-paddingleft-2{padding-left:40px}');
                        customCss.push('li.list-'+p+'-paddingleft-3{padding-left:55px}');
                        break;
                    case 'cn2':
                        customCss.push('li.list-'+p+'-paddingleft-1{padding-left:40px}');
                        customCss.push('li.list-'+p+'-paddingleft-2{padding-left:55px}');
                        customCss.push('li.list-'+p+'-paddingleft-3{padding-left:68px}');
                        break;
                    case 'num':
                    case 'num1':
                        customCss.push('li.list-'+p+'-paddingleft-1{padding-left:25px}');
                        break;
                    case 'num2':
                        customCss.push('li.list-'+p+'-paddingleft-1{padding-left:35px}');
                        customCss.push('li.list-'+p+'-paddingleft-2{padding-left:40px}');
                        break;
                    case 'dash':
                        customCss.push('li.list-'+p+'-paddingleft{padding-left:35px}');
                        break;
                    case 'dot':
                        customCss.push('li.list-'+p+'-paddingleft{padding-left:20px}');
                }
            }
            customCss.push('.list-paddingleft-1{padding-left:0}');
            customCss.push('.list-paddingleft-2{padding-left:'+me.options.listDefaultPaddingLeft+'px}');
            customCss.push('.list-paddingleft-3{padding-left:'+me.options.listDefaultPaddingLeft*2+'px}');
            //如果不给宽度会在自定应样式里出现滚动条
            utils.cssRule('list', 'ol,ul{margin:0;pading:0;'+(browser.ie ? '' : 'width:95%')+'}li{clear:both;}'+customCss.join('\n'), me.document);
        });
        //单独处理剪切的问题
        me.ready(function(){
            domUtils.on(me.body,'cut',function(){
                setTimeout(function(){
                    var rng = me.selection.getRange(),li;
                    //trace:3416
                    if(!rng.collapsed){
                        if(li = domUtils.findParentByTagName(rng.startContainer,'li',true)){
                            if(!li.nextSibling && domUtils.isEmptyBlock(li)){
                                var pn = li.parentNode,node;
                                if(node = pn.previousSibling){
                                    domUtils.remove(pn);
                                    rng.setStartAtLast(node).collapse(true);
                                    rng.select(true);
                                }else if(node = pn.nextSibling){
                                    domUtils.remove(pn);
                                    rng.setStartAtFirst(node).collapse(true);
                                    rng.select(true);
                                }else{
                                    var tmpNode = me.document.createElement('p');
                                    domUtils.fillNode(me.document,tmpNode);
                                    pn.parentNode.insertBefore(tmpNode,pn);
                                    domUtils.remove(pn);
                                    rng.setStart(tmpNode,0).collapse(true);
                                    rng.select(true);
                                }
                            }
                        }
                    }

                })
            })
        });

        function getStyle(node){
            var cls = node.className;
            if(domUtils.hasClass(node,/custom_/)){
                return cls.match(/custom_(\w+)/)[1]
            }
            return domUtils.getStyle(node, 'list-style-type')

        }

        me.addListener('beforepaste',function(type,html){
            var me = this,
                rng = me.selection.getRange(),li;
            var root = UE.htmlparser(html.html,true);
            if(li = domUtils.findParentByTagName(rng.startContainer,'li',true)){
                var list = li.parentNode,tagName = list.tagName == 'OL' ? 'ul':'ol';
                utils.each(root.getNodesByTagName(tagName),function(n){
                    n.tagName = list.tagName;
                    n.setAttr();
                    if(n.parentNode === root){
                        type = getStyle(list) || (list.tagName == 'OL' ? 'decimal' : 'disc')
                    }else{
                        var className = n.parentNode.getAttr('class');
                        if(className && /custom_/.test(className)){
                            type = className.match(/custom_(\w+)/)[1]
                        }else{
                            type = n.parentNode.getStyle('list-style-type');
                        }
                        if(!type){
                            type = list.tagName == 'OL' ? 'decimal' : 'disc';
                        }
                    }
                    var index = utils.indexOf(listStyle[list.tagName], type);
                    if(n.parentNode !== root)
                        index = index + 1 == listStyle[list.tagName].length ? 0 : index + 1;
                    var currentStyle = listStyle[list.tagName][index];
                    if(customStyle[currentStyle]){
                        n.setAttr('class', 'custom_' + currentStyle)

                    }else{
                        n.setStyle('list-style-type',currentStyle)
                    }
                })

            }

            html.html = root.toHtml();
        });
        //导出时，去掉p标签
        me.getOpt('disablePInList') === true && me.addOutputRule(function(root){
            utils.each(root.getNodesByTagName('li'),function(li){
                var newChildrens = [],index=0;
                utils.each(li.children,function(n){
                    if(n.tagName == 'p'){
                        var tmpNode;
                        while(tmpNode = n.children.pop()) {
                            newChildrens.splice(index,0,tmpNode);
                            tmpNode.parentNode = li;
                            lastNode = tmpNode;
                        }
                        tmpNode = newChildrens[newChildrens.length-1];
                        if(!tmpNode || tmpNode.type != 'element' || tmpNode.tagName != 'br'){
                            var br = UE.uNode.createElement('br');
                            br.parentNode = li;
                            newChildrens.push(br);
                        }

                        index = newChildrens.length;
                    }
                });
                if(newChildrens.length){
                    li.children = newChildrens;
                }
            });
        });
        //进入编辑器的li要套p标签
        me.addInputRule(function(root){
            utils.each(root.getNodesByTagName('li'),function(li){
                var tmpP = UE.uNode.createElement('p');
                for(var i= 0,ci;ci=li.children[i];){
                    if(ci.type == 'text' || dtd.p[ci.tagName]){
                        tmpP.appendChild(ci);
                    }else{
                        if(tmpP.firstChild()){
                            li.insertBefore(tmpP,ci);
                            tmpP = UE.uNode.createElement('p');
                            i = i + 2;
                        }else{
                            i++;
                        }

                    }
                }
                if(tmpP.firstChild() && !tmpP.parentNode || !li.firstChild()){
                    li.appendChild(tmpP);
                }
                //trace:3357
                //p不能为空
                if (!tmpP.firstChild()) {
                    tmpP.innerHTML(browser.ie ? '&nbsp;' : '<br/>')
                }
                //去掉末尾的空白
                var p = li.firstChild();
                var lastChild = p.lastChild();
                if(lastChild && lastChild.type == 'text' && /^\s*$/.test(lastChild.data)){
                    p.removeChild(lastChild)
                }
            });
            if(me.options.autoTransWordToList){
                var orderlisttype = {
                        'num1':/^\d+\)/,
                        'decimal':/^\d+\./,
                        'lower-alpha':/^[a-z]+\)/,
                        'upper-alpha':/^[A-Z]+\./,
                        'cn':/^[\u4E00\u4E8C\u4E09\u56DB\u516d\u4e94\u4e03\u516b\u4e5d]+[\u3001]/,
                        'cn2':/^\([\u4E00\u4E8C\u4E09\u56DB\u516d\u4e94\u4e03\u516b\u4e5d]+\)/
                    },
                    unorderlisttype = {
                        'square':'n'
                    };
                function checkListType(content,container){
                    var span = container.firstChild();
                    if(span &&  span.type == 'element' && span.tagName == 'span' && /Wingdings|Symbol/.test(span.getStyle('font-family'))){
                        for(var p in unorderlisttype){
                            if(unorderlisttype[p] == span.data){
                                return p
                            }
                        }
                        return 'disc'
                    }
                    for(var p in orderlisttype){
                        if(orderlisttype[p].test(content)){
                            return p;
                        }
                    }

                }
                utils.each(root.getNodesByTagName('p'),function(node){
                    if(node.getAttr('class') != 'MsoListParagraph'){
                        return
                    }

                    //word粘贴过来的会带有margin要去掉,但这样也可能会误命中一些央视
                    node.setStyle('margin','');
                    node.setStyle('margin-left','');
                    node.setAttr('class','');

                    function appendLi(list,p,type){
                        if(list.tagName == 'ol'){
                            if(browser.ie){
                                var first = p.firstChild();
                                if(first.type =='element' && first.tagName == 'span' && orderlisttype[type].test(first.innerText())){
                                    p.removeChild(first);
                                }
                            }else{
                                p.innerHTML(p.innerHTML().replace(orderlisttype[type],''));
                            }
                        }else{
                            p.removeChild(p.firstChild())
                        }

                        var li = UE.uNode.createElement('li');
                        li.appendChild(p);
                        list.appendChild(li);
                    }
                    var tmp = node,type,cacheNode = node;

                    if(node.parentNode.tagName != 'li' && (type = checkListType(node.innerText(),node))){

                        var list = UE.uNode.createElement(me.options.insertorderedlist.hasOwnProperty(type) ? 'ol' : 'ul');
                        if(customStyle[type]){
                            list.setAttr('class','custom_'+type)
                        }else{
                            list.setStyle('list-style-type',type)
                        }
                        while(node && node.parentNode.tagName != 'li' && checkListType(node.innerText(),node)){
                            tmp = node.nextSibling();
                            if(!tmp){
                                node.parentNode.insertBefore(list,node)
                            }
                            appendLi(list,node,type);
                            node = tmp;
                        }
                        if(!list.parentNode && node && node.parentNode){
                            node.parentNode.insertBefore(list,node)
                        }
                    }
                    var span = cacheNode.firstChild();
                    if(span && span.type == 'element' && span.tagName == 'span' && /^\s*(&nbsp;)+\s*$/.test(span.innerText())){
                        span.parentNode.removeChild(span)
                    }
                })
            }

        });

        //调整索引标签
        me.addListener('contentchange',function(){
            adjustListStyle(me.document)
        });

        function adjustListStyle(doc,ignore){
            utils.each(domUtils.getElementsByTagName(doc,'ol ul'),function(node){

                if(!domUtils.inDoc(node,doc))
                    return;

                var parent = node.parentNode;
                if(parent.tagName == node.tagName){
                    var nodeStyleType = getStyle(node) || (node.tagName == 'OL' ? 'decimal' : 'disc'),
                        parentStyleType = getStyle(parent) || (parent.tagName == 'OL' ? 'decimal' : 'disc');
                    if(nodeStyleType == parentStyleType){
                        var styleIndex = utils.indexOf(listStyle[node.tagName], nodeStyleType);
                        styleIndex = styleIndex + 1 == listStyle[node.tagName].length ? 0 : styleIndex + 1;
                        setListStyle(node,listStyle[node.tagName][styleIndex])
                    }

                }
                var index = 0,type = 2;
                if( domUtils.hasClass(node,/custom_/)){
                    if(!(/[ou]l/i.test(parent.tagName) && domUtils.hasClass(parent,/custom_/))){
                        type = 1;
                    }
                }else{
                    if(/[ou]l/i.test(parent.tagName) && domUtils.hasClass(parent,/custom_/)){
                        type = 3;
                    }
                }

                var style = domUtils.getStyle(node, 'list-style-type');
                style && (node.style.cssText = 'list-style-type:' + style);
                node.className = utils.trim(node.className.replace(/list-paddingleft-\w+/,'')) + ' list-paddingleft-' + type;
                utils.each(domUtils.getElementsByTagName(node,'li'),function(li){
                    li.style.cssText && (li.style.cssText = '');
                    if(!li.firstChild){
                        domUtils.remove(li);
                        return;
                    }
                    if(li.parentNode !== node){
                        return;
                    }
                    index++;
                    if(domUtils.hasClass(node,/custom_/) ){
                        var paddingLeft = 1,currentStyle = getStyle(node);
                        if(node.tagName == 'OL'){
                            if(currentStyle){
                                switch(currentStyle){
                                    case 'cn' :
                                    case 'cn1':
                                    case 'cn2':
                                        if(index > 10 && (index % 10 == 0 || index > 10 && index < 20)){
                                            paddingLeft = 2
                                        }else if(index > 20){
                                            paddingLeft = 3
                                        }
                                        break;
                                    case 'num2' :
                                        if(index > 9){
                                            paddingLeft = 2
                                        }
                                }
                            }
                            li.className = 'list-'+customStyle[currentStyle]+ index + ' ' + 'list-'+currentStyle+'-paddingleft-' + paddingLeft;
                        }else{
                            li.className = 'list-'+customStyle[currentStyle]  + ' ' + 'list-'+currentStyle+'-paddingleft';
                        }
                    }else{
                        li.className = li.className.replace(/list-[\w\-]+/gi,'');
                    }
                    var className = li.getAttribute('class');
                    if(className !== null && !className.replace(/\s/g,'')){
                        domUtils.removeAttributes(li,'class')
                    }
                });
                !ignore && adjustList(node,node.tagName.toLowerCase(),getStyle(node)||domUtils.getStyle(node, 'list-style-type'),true);
            })
        }
        function adjustList(list, tag, style,ignoreEmpty) {
            var nextList = list.nextSibling;
            if (nextList && nextList.nodeType == 1 && nextList.tagName.toLowerCase() == tag && (getStyle(nextList) || domUtils.getStyle(nextList, 'list-style-type') || (tag == 'ol' ? 'decimal' : 'disc')) == style) {
                domUtils.moveChild(nextList, list);
                if (nextList.childNodes.length == 0) {
                    domUtils.remove(nextList);
                }
            }
            if(nextList && domUtils.isFillChar(nextList)){
                domUtils.remove(nextList);
            }
            var preList = list.previousSibling;
            if (preList && preList.nodeType == 1 && preList.tagName.toLowerCase() == tag && (getStyle(preList) || domUtils.getStyle(preList, 'list-style-type') || (tag == 'ol' ? 'decimal' : 'disc')) == style) {
                domUtils.moveChild(list, preList);
            }
            if(preList && domUtils.isFillChar(preList)){
                domUtils.remove(preList);
            }
            !ignoreEmpty && domUtils.isEmptyBlock(list) && domUtils.remove(list);
            if(getStyle(list)){
                adjustListStyle(list.ownerDocument,true)
            }
        }

        function setListStyle(list,style){
            if(customStyle[style]){
                list.className = 'custom_' + style;
            }
            try{
                domUtils.setStyle(list, 'list-style-type', style);
            }catch(e){}
        }
        function clearEmptySibling(node) {
            var tmpNode = node.previousSibling;
            if (tmpNode && domUtils.isEmptyBlock(tmpNode)) {
                domUtils.remove(tmpNode);
            }
            tmpNode = node.nextSibling;
            if (tmpNode && domUtils.isEmptyBlock(tmpNode)) {
                domUtils.remove(tmpNode);
            }
        }

        me.addListener('keydown', function (type, evt) {
            function preventAndSave() {
                evt.preventDefault ? evt.preventDefault() : (evt.returnValue = false);
                me.fireEvent('contentchange');
                me.undoManger && me.undoManger.save();
            }
            function findList(node,filterFn){
                while(node && !domUtils.isBody(node)){
                    if(filterFn(node)){
                        return null
                    }
                    if(node.nodeType == 1 && /[ou]l/i.test(node.tagName)){
                        return node;
                    }
                    node = node.parentNode;
                }
                return null;
            }
            var keyCode = evt.keyCode || evt.which;
            if (keyCode == 13 && !evt.shiftKey) {//回车
                var rng = me.selection.getRange(),
                    parent = domUtils.findParent(rng.startContainer,function(node){return domUtils.isBlockElm(node)},true),
                    li = domUtils.findParentByTagName(rng.startContainer,'li',true);
                if(parent && parent.tagName != 'PRE' && !li){
                    var html = parent.innerHTML.replace(new RegExp(domUtils.fillChar, 'g'),'');
                    if(/^\s*1\s*\.[^\d]/.test(html)){
                        parent.innerHTML = html.replace(/^\s*1\s*\./,'');
                        rng.setStartAtLast(parent).collapse(true).select();
                        me.__hasEnterExecCommand = true;
                        me.execCommand('insertorderedlist');
                        me.__hasEnterExecCommand = false;
                    }
                }
                var range = me.selection.getRange(),
                    start = findList(range.startContainer,function (node) {
                        return node.tagName == 'TABLE';
                    }),
                    end = range.collapsed ? start : findList(range.endContainer,function (node) {
                        return node.tagName == 'TABLE';
                    });

                if (start && end && start === end) {

                    if (!range.collapsed) {
                        start = domUtils.findParentByTagName(range.startContainer, 'li', true);
                        end = domUtils.findParentByTagName(range.endContainer, 'li', true);
                        if (start && end && start === end) {
                            range.deleteContents();
                            li = domUtils.findParentByTagName(range.startContainer, 'li', true);
                            if (li && domUtils.isEmptyBlock(li)) {

                                pre = li.previousSibling;
                                next = li.nextSibling;
                                p = me.document.createElement('p');

                                domUtils.fillNode(me.document, p);
                                parentList = li.parentNode;
                                if (pre && next) {
                                    range.setStart(next, 0).collapse(true).select(true);
                                    domUtils.remove(li);

                                } else {
                                    if (!pre && !next || !pre) {

                                        parentList.parentNode.insertBefore(p, parentList);


                                    } else {
                                        li.parentNode.parentNode.insertBefore(p, parentList.nextSibling);
                                    }
                                    domUtils.remove(li);
                                    if (!parentList.firstChild) {
                                        domUtils.remove(parentList);
                                    }
                                    range.setStart(p, 0).setCursor();


                                }
                                preventAndSave();
                                return;

                            }
                        } else {
                            var tmpRange = range.cloneRange(),
                                bk = tmpRange.collapse(false).createBookmark();

                            range.deleteContents();
                            tmpRange.moveToBookmark(bk);
                            var li = domUtils.findParentByTagName(tmpRange.startContainer, 'li', true);

                            clearEmptySibling(li);
                            tmpRange.select();
                            preventAndSave();
                            return;
                        }
                    }


                    li = domUtils.findParentByTagName(range.startContainer, 'li', true);

                    if (li) {
                        if (domUtils.isEmptyBlock(li)) {
                            bk = range.createBookmark();
                            var parentList = li.parentNode;
                            if (li !== parentList.lastChild) {
                                domUtils.breakParent(li, parentList);
                                clearEmptySibling(li);
                            } else {

                                parentList.parentNode.insertBefore(li, parentList.nextSibling);
                                if (domUtils.isEmptyNode(parentList)) {
                                    domUtils.remove(parentList);
                                }
                            }
                            //嵌套不处理
                            if (!dtd.$list[li.parentNode.tagName]) {

                                if (!domUtils.isBlockElm(li.firstChild)) {
                                    p = me.document.createElement('p');
                                    li.parentNode.insertBefore(p, li);
                                    while (li.firstChild) {
                                        p.appendChild(li.firstChild);
                                    }
                                    domUtils.remove(li);
                                } else {
                                    domUtils.remove(li, true);
                                }
                            }
                            range.moveToBookmark(bk).select();


                        } else {
                            var first = li.firstChild;
                            if (!first || !domUtils.isBlockElm(first)) {
                                var p = me.document.createElement('p');

                                !li.firstChild && domUtils.fillNode(me.document, p);
                                while (li.firstChild) {

                                    p.appendChild(li.firstChild);
                                }
                                li.appendChild(p);
                                first = p;
                            }

                            var span = me.document.createElement('span');

                            range.insertNode(span);
                            domUtils.breakParent(span, li);

                            var nextLi = span.nextSibling;
                            first = nextLi.firstChild;

                            if (!first) {
                                p = me.document.createElement('p');

                                domUtils.fillNode(me.document, p);
                                nextLi.appendChild(p);
                                first = p;
                            }
                            if (domUtils.isEmptyNode(first)) {
