                        if ( subMenu.length ) {
                            function getLabel(){
                                switch (item.icon){
                                    case "table":
                                        return me.getLang( "contextMenu.table" );
                                    case "justifyjustify":
                                        return me.getLang( "contextMenu.paragraph" );
                                    case "aligntd":
