                        },
                        {
                            cmdName:'cellalignment',
                            value:{align:'left',vAlign:'bottom'}
                        },
                        {
                            cmdName:'cellalignment',
                            value:{align:'center',vAlign:'bottom'}
                        },
                        {
                            cmdName:'cellalignment',
                            value:{align:'right',vAlign:'bottom'}
                        }
                    ]
                },
                {
                    group:lang.aligntable,
                    icon:'aligntable',
                    subMenu:[
                        {
                            cmdName:'tablealignment',
                            className: 'left',
                            label:lang.tableleft,
                            value:"left"
                        },
                        {
                            cmdName:'tablealignment',
                            className: 'center',
                            label:lang.tablecenter,
                            value:"center"
                        },
                        {
                            cmdName:'tablealignment',
                            className: 'right',
                            label:lang.tableright,
                            value:"right"
                        }
                    ]
                },
                '-',
                {
                    label:lang.insertparagraphbefore,
                    cmdName:'insertparagraph',
                    value:true
                },
                {
                    label:lang.insertparagraphafter,
                    cmdName:'insertparagraph'
                },
                {
                    label:lang['copy'],
                    cmdName:'copy'
                },
                {
                    label:lang['paste'],
                    cmdName:'paste'
                }
            ];
        if ( !items.length ) {
            return;
        }
        var uiUtils = UE.ui.uiUtils;

        me.addListener( 'contextmenu', function ( type, evt ) {

            var offset = uiUtils.getViewportOffsetByEvent( evt );
            me.fireEvent( 'beforeselectionchange' );
            if ( menu ) {
                menu.destroy();
            }
            for ( var i = 0, ti, contextItems = []; ti = items[i]; i++ ) {
                var last;
                (function ( item ) {
                    if ( item == '-' ) {
                        if ( (last = contextItems[contextItems.length - 1 ] ) && last !== '-' ) {
                            contextItems.push( '-' );
                        }
                    } else if ( item.hasOwnProperty( "group" ) ) {
                        for ( var j = 0, cj, subMenu = []; cj = item.subMenu[j]; j++ ) {
                            (function ( subItem ) {
                                if ( subItem == '-' ) {
                                    if ( (last = subMenu[subMenu.length - 1 ] ) && last !== '-' ) {
                                        subMenu.push( '-' );
                                    }else{
                                        subMenu.splice(subMenu.length-1);
                                    }
                                } else {
                                    if ( (me.commands[subItem.cmdName] || UE.commands[subItem.cmdName] || subItem.query) &&
                                        (subItem.query ? subItem.query() : me.queryCommandState( subItem.cmdName )) > -1 ) {
                                        subMenu.push( {
                                            'label':subItem.label || me.getLang( "contextMenu." + subItem.cmdName + (subItem.value || '') )||"",
                                            'className':'edui-for-' +subItem.cmdName + ( subItem.className ? ( ' edui-for-' + subItem.cmdName + '-' + subItem.className ) : '' ),
                                            onclick:subItem.exec ? function () {
                                                subItem.exec.call( me );
                                            } : function () {
                                                me.execCommand( subItem.cmdName, subItem.value );
                                            }
                                        } );
                                    }
                                }
                            })( cj );
                        }
                        if ( subMenu.length ) {
                            function getLabel(){
                                switch (item.icon){
                                    case "table":
                                        return me.getLang( "contextMenu.table" );
                                    case "justifyjustify":
                                        return me.getLang( "contextMenu.paragraph" );
                                    case "aligntd":
                                        return me.getLang("contextMenu.aligntd");
                                    case "aligntable":
                                        return me.getLang("contextMenu.aligntable");
                                    case "tablesort":
                                        return lang.tablesort;
                                    case "borderBack":
                                        return lang.borderbk;
                                    default :
                                        return '';
                                }
                            }
                            contextItems.push( {
                                //todo 修正成自动获取方式
                                'label':getLabel(),
                                className:'edui-for-' + item.icon,
                                'subMenu':{
                                    items:subMenu,
                                    editor:me
                                }
                            } );
                        }

                    } else {
                        //有可能commmand没有加载右键不能出来，或者没有command也想能展示出来添加query方法
                        if ( (me.commands[item.cmdName] || UE.commands[item.cmdName] || item.query) &&
                            (item.query ? item.query.call(me) : me.queryCommandState( item.cmdName )) > -1 ) {

                            contextItems.push( {
                                'label':item.label || me.getLang( "contextMenu." + item.cmdName ),
                                className:'edui-for-' + (item.icon ? item.icon : item.cmdName + (item.value || '')),
                                onclick:item.exec ? function () {
                                    item.exec.call( me );
                                } : function () {
                                    me.execCommand( item.cmdName, item.value );
                                }
                            } );
                        }

                    }

                })( ti );
            }
            if ( contextItems[contextItems.length - 1] == '-' ) {
                contextItems.pop();
            }

            menu = new UE.ui.Menu( {
                items:contextItems,
                className:"edui-contextmenu",
                editor:me
            } );
            menu.render();
            menu.showAt( offset );

            me.fireEvent("aftershowcontextmenu",menu);

            domUtils.preventDefault( evt );
            if ( browser.ie ) {
                var ieRange;
                try {
                    ieRange = me.selection.getNative().createRange();
                } catch ( e ) {
                    return;
                }
                if ( ieRange.item ) {
                    var range = new dom.Range( me.document );
                    range.selectNode( ieRange.item( 0 ) ).select( true, true );
                }
            }
        });

        // 添加复制的flash按钮
        me.addListener('aftershowcontextmenu', function(type, menu) {
            if (me.zeroclipboard) {
                var items = menu.items;
                for (var key in items) {
                    if (items[key].className == 'edui-for-copy') {
                        me.zeroclipboard.clip(items[key].getDom());
                    }
                }
            }
        });

    };


// plugins/shortcutmenu.js
///import core
///commands       弹出菜单
// commandsName  popupmenu
///commandsTitle  弹出菜单
    /**
     * 弹出菜单
     * @function
     * @name baidu.editor.plugins.popupmenu
     * @author xuheng
     */

    UE.plugins['shortcutmenu'] = function () {
        var me = this,
            menu,
            items = me.options.shortcutMenu || [];

        if (!items.length) {
            return;
        }

        me.addListener ('contextmenu mouseup' , function (type , e) {
            var me = this,
                customEvt = {
                    type : type ,
                    target : e.target || e.srcElement ,
                    screenX : e.screenX ,
                    screenY : e.screenY ,
                    clientX : e.clientX ,
                    clientY : e.clientY
                };

            setTimeout (function () {
                var rng = me.selection.getRange ();
                if (rng.collapsed === false || type == "contextmenu") {

                    if (!menu) {
                        menu = new baidu.editor.ui.ShortCutMenu ({
                            editor : me ,
                            items : items ,
                            theme : me.options.theme ,
                            className : 'edui-shortcutmenu'
                        });

                        menu.render ();
                        me.fireEvent ("afterrendershortcutmenu" , menu);
                    }

                    menu.show (customEvt , !!UE.plugins['contextmenu']);
                }
            });

            if (type == 'contextmenu') {
                domUtils.preventDefault (e);
                if (browser.ie9below) {
                    var ieRange;
                    try {
                        ieRange = me.selection.getNative().createRange();
                    } catch (e) {
                        return;
                    }
                    if (ieRange.item) {
                        var range = new dom.Range (me.document);
                        range.selectNode (ieRange.item (0)).select (true , true);

                    }
                }
            }
        });

        me.addListener ('keydown' , function (type) {
            if (type == "keydown") {
                menu && !menu.isHidden && menu.hide ();
            }

        });

    };




// plugins/basestyle.js
    /**
     * B、I、sub、super命令支持
     * @file
     * @since 1.2.6.1
     */

    UE.plugins['basestyle'] = function(){

        /**
         * 字体加粗
         * @command bold
         * @param { String } cmd 命令字符串
         * @remind 对已加粗的文本内容执行该命令， 将取消加粗
         * @method execCommand
         * @example
         * ```javascript
         * //editor是编辑器实例
         * //对当前选中的文本内容执行加粗操作
         * //第一次执行， 文本内容加粗
         * editor.execCommand( 'bold' );
         *
         * //第二次执行， 文本内容取消加粗
         * editor.execCommand( 'bold' );
         * ```
         */


        /**
         * 字体倾斜
         * @command italic
         * @method execCommand
         * @param { String } cmd 命令字符串
         * @remind 对已倾斜的文本内容执行该命令， 将取消倾斜
         * @example
         * ```javascript
         * //editor是编辑器实例
         * //对当前选中的文本内容执行斜体操作
         * //第一次操作， 文本内容将变成斜体
         * editor.execCommand( 'italic' );
         *
         * //再次对同一文本内容执行， 则文本内容将恢复正常
         * editor.execCommand( 'italic' );
         * ```
         */

        /**
         * 下标文本，与“superscript”命令互斥
         * @command subscript
         * @method execCommand
         * @remind  把选中的文本内容切换成下标文本， 如果当前选中的文本已经是下标， 则该操作会把文本内容还原成正常文本
         * @param { String } cmd 命令字符串
         * @example
         * ```javascript
         * //editor是编辑器实例
         * //对当前选中的文本内容执行下标操作
         * //第一次操作， 文本内容将变成下标文本
         * editor.execCommand( 'subscript' );
         *
         * //再次对同一文本内容执行， 则文本内容将恢复正常
         * editor.execCommand( 'subscript' );
         * ```
         */

        /**
         * 上标文本，与“subscript”命令互斥
         * @command superscript
         * @method execCommand
         * @remind 把选中的文本内容切换成上标文本， 如果当前选中的文本已经是上标， 则该操作会把文本内容还原成正常文本
         * @param { String } cmd 命令字符串
         * @example
         * ```javascript
         * //editor是编辑器实例
         * //对当前选中的文本内容执行上标操作
         * //第一次操作， 文本内容将变成上标文本
         * editor.execCommand( 'superscript' );
         *
         * //再次对同一文本内容执行， 则文本内容将恢复正常
         * editor.execCommand( 'superscript' );
         * ```
         */
        var basestyles = {
                'bold':['strong','b'],
                'italic':['em','i'],
                'subscript':['sub'],
                'superscript':['sup']
            },
            getObj = function(editor,tagNames){
                return domUtils.filterNodeList(editor.selection.getStartElementPath(),tagNames);
            },
            me = this;
        //添加快捷键
        me.addshortcutkey({
            "Bold" : "ctrl+66",//^B
            "Italic" : "ctrl+73", //^I
            "Underline" : "ctrl+85"//^U
        });
        me.addInputRule(function(root){
            utils.each(root.getNodesByTagName('b i'),function(node){
                switch (node.tagName){
                    case 'b':
                        node.tagName = 'strong';
                        break;
                    case 'i':
                        node.tagName = 'em';
                }
            });
        });
        for ( var style in basestyles ) {
            (function( cmd, tagNames ) {
                me.commands[cmd] = {
                    execCommand : function( cmdName ) {
                        var range = me.selection.getRange(),obj = getObj(this,tagNames);
                        if ( range.collapsed ) {
                            if ( obj ) {
                                var tmpText =  me.document.createTextNode('');
                                range.insertNode( tmpText ).removeInlineStyle( tagNames );
                                range.setStartBefore(tmpText);
                                domUtils.remove(tmpText);
                            } else {
                                var tmpNode = range.document.createElement( tagNames[0] );
                                if(cmdName == 'superscript' || cmdName == 'subscript'){
                                    tmpText = me.document.createTextNode('');
                                    range.insertNode(tmpText)
                                        .removeInlineStyle(['sub','sup'])
                                        .setStartBefore(tmpText)
                                        .collapse(true);
                                }
                                range.insertNode( tmpNode ).setStart( tmpNode, 0 );
                            }
                            range.collapse( true );
                        } else {
                            if(cmdName == 'superscript' || cmdName == 'subscript'){
                                if(!obj || obj.tagName.toLowerCase() != cmdName){
                                    range.removeInlineStyle(['sub','sup']);
                                }
                            }
                            obj ? range.removeInlineStyle( tagNames ) : range.applyInlineStyle( tagNames[0] );
                        }
                        range.select();
                    },
                    queryCommandState : function() {
                        return getObj(this,tagNames) ? 1 : 0;
                    }
                };
            })( style, basestyles[style] );
        }
    };



// plugins/elementpath.js
    /**
     * 选取路径命令
     * @file
     */
    UE.plugins['elementpath'] = function(){
        var currentLevel,
            tagNames,
            me = this;
        me.setOpt('elementPathEnabled',true);
        if(!me.options.elementPathEnabled){
            return;
        }
        me.commands['elementpath'] = {
            execCommand : function( cmdName, level ) {
                var start = tagNames[level],
                    range = me.selection.getRange();
                currentLevel = level*1;
                range.selectNode(start).select();
            },
            queryCommandValue : function() {
                //产生一个副本，不能修改原来的startElementPath;
                var parents = [].concat(this.selection.getStartElementPath()).reverse(),
                    names = [];
                tagNames = parents;
                for(var i=0,ci;ci=parents[i];i++){
                    if(ci.nodeType == 3) {
                        continue;
                    }
                    var name = ci.tagName.toLowerCase();
                    if(name == 'img' && ci.getAttribute('anchorname')){
                        name = 'anchor';
                    }
                    names[i] = name;
                    if(currentLevel == i){
                        currentLevel = -1;
                        break;
                    }
                }
                return names;
            }
        };
    };



// plugins/formatmatch.js
    /**
     * 格式刷，只格式inline的
     * @file
     * @since 1.2.6.1
     */

    /**
     * 格式刷
     * @command formatmatch
     * @method execCommand
     * @remind 该操作不能复制段落格式
     * @param { String } cmd 命令字符串
     * @example
     * ```javascript
     * //editor是编辑器实例
     * //获取格式刷
     * editor.execCommand( 'formatmatch' );
     * ```
     */
    UE.plugins['formatmatch'] = function(){

        var me = this,
            list = [],img,
            flag = 0;

        me.addListener('reset',function(){
            list = [];
            flag = 0;
        });

        function addList(type,evt){

            if(browser.webkit){
                var target = evt.target.tagName == 'IMG' ? evt.target : null;
            }

            function addFormat(range){

                if(text){
                    range.selectNode(text);
                }
                return range.applyInlineStyle(list[list.length-1].tagName,null,list);

            }

            me.undoManger && me.undoManger.save();

            var range = me.selection.getRange(),
                imgT = target || range.getClosedNode();
            if(img && imgT && imgT.tagName == 'IMG'){
                //trace:964

                imgT.style.cssText += ';float:' + (img.style.cssFloat || img.style.styleFloat ||'none') + ';display:' + (img.style.display||'inline');

                img = null;
            }else{
                if(!img){
                    var collapsed = range.collapsed;
                    if(collapsed){
                        var text = me.document.createTextNode('match');
                        range.insertNode(text).select();


                    }
                    me.__hasEnterExecCommand = true;
                    //不能把block上的属性干掉
                    //trace:1553
                    var removeFormatAttributes = me.options.removeFormatAttributes;
                    me.options.removeFormatAttributes = '';
                    me.execCommand('removeformat');
                    me.options.removeFormatAttributes = removeFormatAttributes;
                    me.__hasEnterExecCommand = false;
                    //trace:969
                    range = me.selection.getRange();
                    if(list.length){
                        addFormat(range);
                    }
                    if(text){
                        range.setStartBefore(text).collapse(true);

                    }
                    range.select();
                    text && domUtils.remove(text);
                }

            }




            me.undoManger && me.undoManger.save();
            me.removeListener('mouseup',addList);
            flag = 0;
        }

        me.commands['formatmatch'] = {
            execCommand : function( cmdName ) {

                if(flag){
                    flag = 0;
                    list = [];
                    me.removeListener('mouseup',addList);
                    return;
                }



                var range = me.selection.getRange();
                img = range.getClosedNode();
                if(!img || img.tagName != 'IMG'){
                    range.collapse(true).shrinkBoundary();
                    var start = range.startContainer;
                    list = domUtils.findParents(start,true,function(node){
                        return !domUtils.isBlockElm(node) && node.nodeType == 1;
                    });
                    //a不能加入格式刷, 并且克隆节点
                    for(var i=0,ci;ci=list[i];i++){
                        if(ci.tagName == 'A'){
                            list.splice(i,1);
                            break;
                        }
                    }

                }

                me.addListener('mouseup',addList);
                flag = 1;


            },
            queryCommandState : function() {
                return flag;
            },
            notNeedUndo : 1
        };
    };



// plugins/searchreplace.js
///import core
///commands 查找替换
///commandsName  SearchReplace
///commandsTitle  查询替换
///commandsDialog  dialogs\searchreplace
    /**
     * @description 查找替换
     * @author zhanyi
     */

    UE.plugin.register('searchreplace',function(){
        var me = this;

        var _blockElm = {'table':1,'tbody':1,'tr':1,'ol':1,'ul':1};

        function findTextInString(textContent,opt,currentIndex){
            var str = opt.searchStr;
            if(opt.dir == -1){
                textContent = textContent.split('').reverse().join('');
                str = str.split('').reverse().join('');
                currentIndex = textContent.length - currentIndex;

            }
            var reg = new RegExp(str,'g' + (opt.casesensitive ? '' : 'i')),match;

            while(match = reg.exec(textContent)){
                if(match.index >= currentIndex){
                    return opt.dir == -1 ? textContent.length - match.index - opt.searchStr.length : match.index;
                }
            }
            return  -1
        }
        function findTextBlockElm(node,currentIndex,opt){
            var textContent,index,methodName = opt.all || opt.dir == 1 ? 'getNextDomNode' : 'getPreDomNode';
            if(domUtils.isBody(node)){
                node = node.firstChild;
            }
            var first = 1;
            while(node){
                textContent = node.nodeType == 3 ? node.nodeValue : node[browser.ie ? 'innerText' : 'textContent'];
                index = findTextInString(textContent,opt,currentIndex );
                first = 0;
                if(index!=-1){
                    return {
                        'node':node,
                        'index':index
                    }
                }
                node = domUtils[methodName](node);
                while(node && _blockElm[node.nodeName.toLowerCase()]){
                    node = domUtils[methodName](node,true);
                }
                if(node){
                    currentIndex = opt.dir == -1 ? (node.nodeType == 3 ? node.nodeValue : node[browser.ie ? 'innerText' : 'textContent']).length : 0;
                }

            }
        }
        function findNTextInBlockElm(node,index,str){
            var currentIndex = 0,
                currentNode = node.firstChild,
                currentNodeLength = 0,
                result;
            while(currentNode){
                if(currentNode.nodeType == 3){
                    currentNodeLength = currentNode.nodeValue.replace(/(^[\t\r\n]+)|([\t\r\n]+$)/,'').length;
                    currentIndex += currentNodeLength;
                    if(currentIndex >= index){
                        return {
                            'node':currentNode,
                            'index': currentNodeLength - (currentIndex - index)
                        }
                    }
                }else if(!dtd.$empty[currentNode.tagName]){
                    currentNodeLength = currentNode[browser.ie ? 'innerText' : 'textContent'].replace(/(^[\t\r\n]+)|([\t\r\n]+$)/,'').length
                    currentIndex += currentNodeLength;
                    if(currentIndex >= index){
                        result = findNTextInBlockElm(currentNode,currentNodeLength - (currentIndex - index),str);
                        if(result){
                            return result;
                        }
                    }
                }
                currentNode = domUtils.getNextDomNode(currentNode);

            }
        }

        function searchReplace(me,opt){

            var rng = me.selection.getRange(),
                startBlockNode,
                searchStr = opt.searchStr,
                span = me.document.createElement('span');
            span.innerHTML = '$$ueditor_searchreplace_key$$';

            rng.shrinkBoundary(true);

            //判断是不是第一次选中
            if(!rng.collapsed){
                rng.select();
                var rngText = me.selection.getText();
                if(new RegExp('^' + opt.searchStr + '$',(opt.casesensitive ? '' : 'i')).test(rngText)){
                    if(opt.replaceStr != undefined){
                        replaceText(rng,opt.replaceStr);
                        rng.select();
                        return true;
                    }else{
                        rng.collapse(opt.dir == -1)
                    }

                }
            }


            rng.insertNode(span);
            rng.enlargeToBlockElm(true);
            startBlockNode = rng.startContainer;
            var currentIndex = startBlockNode[browser.ie ? 'innerText' : 'textContent'].indexOf('$$ueditor_searchreplace_key$$');
            rng.setStartBefore(span);
            domUtils.remove(span);
            var result = findTextBlockElm(startBlockNode,currentIndex,opt);
            if(result){
                var rngStart = findNTextInBlockElm(result.node,result.index,searchStr);
                var rngEnd = findNTextInBlockElm(result.node,result.index + searchStr.length,searchStr);
                rng.setStart(rngStart.node,rngStart.index).setEnd(rngEnd.node,rngEnd.index);

                if(opt.replaceStr !== undefined){
                    replaceText(rng,opt.replaceStr)
                }
                rng.select();
                return true;
            }else{
                rng.setCursor()
            }

        }
        function replaceText(rng,str){

            str = me.document.createTextNode(str);
            rng.deleteContents().insertNode(str);

        }
        return {
            commands:{
                'searchreplace':{
                    execCommand:function(cmdName,opt){
                        utils.extend(opt,{
                            all : false,
                            casesensitive : false,
                            dir : 1
                        },true);
                        var num = 0;
                        if(opt.all){

                            var rng = me.selection.getRange(),
                                first = me.body.firstChild;
                            if(first && first.nodeType == 1){
                                rng.setStart(first,0);
                                rng.shrinkBoundary(true);
                            }else if(first.nodeType == 3){
                                rng.setStartBefore(first)
                            }
                            rng.collapse(true).select(true);
                            if(opt.replaceStr !== undefined){
                                me.fireEvent('saveScene');
                            }
                            while(searchReplace(this,opt)){
                                num++;
                            }
                            if(num){
                                me.fireEvent('saveScene');
                            }
                        }else{
                            if(opt.replaceStr !== undefined){
                                me.fireEvent('saveScene');
                            }
                            if(searchReplace(this,opt)){
                                num++
                            }
                            if(num){
                                me.fireEvent('saveScene');
                            }

